import React, { useEffect, useState } from 'react';
import ActivityForm from '../components/ActivityForm';
import ActivityList from '../components/ActivityList';
import Suggestions from '../components/Suggestions';
import { getUserActivities } from '../utils/api';

const Home = ({ userId }) => {
    const [activities, setActivities] = useState([]);

    const fetchActivities = async () => {
        try {
            const response = await getUserActivities(userId);
            setActivities(response.data);
        } catch (error) {
            console.error("Failed to fetch activities:", error);
        }
    };

    useEffect(() => {
        fetchActivities();
    }, [userId]);

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4">
            <h1 className="text-4xl font-bold mb-4 text-center text-green-600">Welcome to the Carbon Footprint Tracker</h1>
            <ActivityForm 
                userId={userId} 
                onActivityAdded={fetchActivities} 
            />
            <div className="flex flex-col md:flex-row justify-between w-full mt-4 gap-4">
                <div className="flex-1 bg-white shadow-md rounded-lg p-6 transition duration-300 ease-in-out transform hover:scale-105">
                    <h2 className="text-2xl font-semibold text-gray-800 mb-4">Your Activities</h2>
                    <ActivityList activities={activities} />
                </div>
                <div className="w-full md:w-1/3 bg-white shadow-md rounded-lg p-6 transition duration-300 ease-in-out transform hover:scale-105">
                    <h2 className="text-2xl font-semibold text-gray-800 mb-4">Suggestions to Reduce Your Footprint</h2>
                    <Suggestions />
                </div>
            </div>
        </div>
    );
};

export default Home;
