// src/pages/Home.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import ActivityForm from '../components/ActivityForm';
import Suggestions from '../components/Suggestions';

const Home = ({ userId, onActivityAdded }) => {
    const navigate = useNavigate();

    const handleGoToDashboard = () => {
        navigate('/dashboard');
    };

    return (
        <div className="flex items-center justify-center flex-col min-h-screen bg-gray-100">
            <h1 className="text-4xl font-bold mb-4 text-center">Welcome to the Carbon Footprint Tracker</h1>
            <ActivityForm userId={userId} onActivityAdded={onActivityAdded} />
            <Suggestions />
            <button
                onClick={handleGoToDashboard}
                className="mt-6 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition"
            >
                Go to Dashboard
            </button>
        </div>
    );
};

export default Home;
