// src/components/SuggestionBall.js
import React, { useEffect, useState } from 'react';
import { getSuggestions } from '../utils/api'; // Ensure this API function is defined and imported

const SuggestionBall = () => {
    const [suggestions, setSuggestions] = useState([]);
    const [currentSuggestion, setCurrentSuggestion] = useState('');
    const [position, setPosition] = useState({ top: 0, left: 0 });

    const fetchSuggestions = async () => {
        const response = await getSuggestions();
        setSuggestions(response.data);
    };

    const updatePosition = () => {
        setPosition({
            top: Math.random() * (window.innerHeight - 100), // Adjust for larger ball size
            left: Math.random() * (window.innerWidth - 100), // Adjust for larger ball size
        });
    };

    const changeSuggestion = () => {
        if (suggestions.length > 0) {
            const randomIndex = Math.floor(Math.random() * suggestions.length);
            setCurrentSuggestion(suggestions[randomIndex]);
        }
    };

    useEffect(() => {
        fetchSuggestions();
        changeSuggestion();
        const intervalId = setInterval(() => {
            changeSuggestion();
            updatePosition();
        }, 6000); // Change suggestion and position every 6 seconds (adjust as needed)

        return () => clearInterval(intervalId); // Cleanup on unmount
    }, [suggestions]);

    return (
        <div
            className="absolute bg-greenCustom text-white rounded-full w-200 h-100 flex items-center justify-center transition-transform" // Increased size
            style={{
                top: position.top,
                left: position.left,
                transition: 'top 0.5s, left 0.5s',
                fontSize: '1rem', // Adjust font size if necessary
                padding: '10px' // Add padding if needed for text
            }}
        >
            {currentSuggestion}
        </div>
    );
};

export default SuggestionBall;
