import React, { useEffect, useState } from 'react';
import ActivityForm from '../components/ActivityForm';
import ActivityList from '../components/ActivityList';
import Suggestions from '../components/Suggestions';
import { getUserActivities } from '../utils/api'; // Ensure this API function is defined and imported

const Home = ({ userId }) => {
    const [activities, setActivities] = useState([]);

    const fetchActivities = async () => {
        try {
            const response = await getUserActivities(userId); // Fetch activities for the user
            setActivities(response.data);
        } catch (error) {
            console.error("Failed to fetch activities:", error);
        }
    };

    useEffect(() => {
        fetchActivities(); // Load activities when the component mounts or userId changes
    }, [userId]);

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4">
            <h1 className="text-4xl font-bold mb-4 text-center">Welcome to the Carbon Footprint Tracker</h1>
            <ActivityForm 
                userId={userId} 
                onActivityAdded={fetchActivities} // Refresh activities when a new activity is added
            />
            <div className="flex flex-row justify-between w-full mt-4">
                <div className="flex-1 mr-4">
                    <ActivityList activities={activities} /> {/* Display the list of activities */}
                </div>
                <div className="w-1/4">
                    <Suggestions /> {/* Display suggestions */}
                </div>
            </div>
        </div>
    );
};

export default Home;
