// src/pages/Home.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import ActivityForm from '../components/ActivityForm';
import Suggestions from '../components/Suggestions';

const Home = ({ userId, onActivityAdded }) => {
    const navigate = useNavigate();

    const handleGoToDashboard = () => {
        navigate('/dashboard');
    };
    const [activities, setActivities] = useState([]);

    const fetchActivities = async () => {
        const response = await getUserActivities(userId);
        setActivities(response.data);
    };

    useEffect(() => {
        fetchActivities();
    }, [userId]);

    return (
        <div>
<div className='flex justify-end'>
<button
                onClick={handleGoToDashboard}
                className=" flex justify-end  mt-6 px-4 py-2 bg-greenCustom text-white rounded-lg hover:bg-greenCustom transition"
            >
               Dashboard
            </button>

</div>

        <div className="flex items-center justify-center flex-col min-h-screen ">

            <h1 className="text-4xl font-bold mb-4 text-center">Welcome to the Carbon Footprint Tracker</h1>
            <ActivityForm userId={userId} onActivityAdded={onActivityAdded} />
            <ActivityList activities={activities} />
            <Suggestions />

        </div>
        </div>
    );
};

export default Home;
