import React, { useState } from 'react';
import { addActivity } from '../utils/api';

const ActivityForm = ({ userId, onActivityAdded }) => {
    const [type, setType] = useState('');
    const [value, setValue] = useState('');
    const [date, setDate] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const activity = { userId, type, value, date };

        try {
            await addActivity(activity); // Add the new activity
            console.log('Activity added:', activity); // Debug log for added activity

            // Call onActivityAdded to refresh the activity list
            if (typeof onActivityAdded === 'function') {
                onActivityAdded();
            } else {
                console.error('onActivityAdded is not a function'); // Error handling
            }

            // Reset form fields
            setType('');
            setValue('');
            setDate('');
        } catch (error) {
            console.error('Error adding activity:', error); // Log errors
        }
    };

    return (
        <div className="flex items-center justify-center w-full mx-auto">
            <form onSubmit={handleSubmit} className="flex flex-row gap-2">
                <input
                    type="text"
                    placeholder="Activity Type"
                    value={type}
                    onChange={(e) => setType(e.target.value)}
                    required
                    className="p-2 border border-gray-300 rounded"
                />
                <input
                    type="number"
                    placeholder="COâ‚‚ Value"
                    value={value}
                    onChange={(e) => setValue(e.target.value)}
                    required
                    className="p-2 border border-gray-300 rounded"
                />
                <input
                    type="date"
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                    required
                    className="p-2 border border-gray-300 rounded"
                />
                <button type="submit" className="p-2 bg-green-500 text-white rounded hover:bg-green-400">
                    Add Activity
                </button>
            </form>
        </div>
    );
};

export default ActivityForm;
